class Solution {public:    string finalString(string s) {        int t=0;        string ans;        while(t<s.size())        {            if(s[t]=='i')            {                ans=reversestring(ans);            }            else            {                ans += s[t];            }            t++;        }        return ans;    }    string reversestring(const string& ans)    {        string temp;        for(int i=ans.size()-1;i>=0;i--)        {            temp += ans[i];        }        return temp;    }};